package FY2023;

import java.math.*;
public class MathFunc {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		System.out.println(Math.min(6.3, 6.0)); //prints minimum value
		
		System.out.println(Math.max(6.3, 6.0)); //prints maximum value
		
		System.out.println(Math.pow(6,8)); //raise the power 6^8
		
		System.out.println(Math.sqrt(15)); //returns square root
		
		System.out.println(Math.cbrt(15)); //returns cube root
		
		System.out.println(Math.abs(-3.5)); //converts negative to positive-> 3.5
		
		System.out.println(Math.floor(5.3)); //round down the closet value 5.0
		
		System.out.println(Math.ceil(5.3)); //round up the close value 6.0
		
		System.out.println(Math.sin(30)); // returns sin value
		
		System.out.println(Math.cos(30));
		
		System.out.println(Math.tan(30));

	}

}
